import { useEffect, useState } from "react";
import { createRoot } from "react-dom/client";
const cohort = "2208-ftb-et-web-ft";
const strangerThings = "https://strangers-things.herokuapp.com/api";

const App = () => {
  const [allPosts, setAllPosts] = useState([]);
  const [newUser, setNewUser] = useState("");

  //cant use API if no useEffect?
  useEffect(() => {
    const getAllData = async () => {
      await getAllPosts();
    };
    getAllData();
  }, []);

  const getAllPosts = async () => {
    const response = await fetch(`${strangerThings}/${cohort}/posts`);
    const data = await response.json();
    setAllPosts(data.data.posts);
  };

  //register user
  const registerUser = async () => {
    console.log("registerUser");
    const response = await fetch(`${strangerThings}/${cohort}/users/register`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        user: {
          username: "jacky1",
          password: "krypton2",
        },
      }),
    });
    const data = await response.json();
    console.log(data);
  };

  registerUser();

  //submit component
  const afterSubmit = () => {
    alert("Hello");
  };

  return (
    <div>
      <h1>Hello from Stranger Things</h1>
      <form>
        Name:{" "}
        <input
          value={newUser}
          onChange={(event) => {
            console.log(event.target.value);
            setNewUser(event.target.value);
          }}
        ></input>
        Password: <input></input>
        <button type='submit'>Register</button>
      </form>
      <div>
        {
          // allPosts.map(post => <div key={post._id})

          allPosts.map((post, i) => {
            return <p key={i}>{post.title}</p>;
          })
        }
      </div>
    </div>
  );
};

const container = document.getElementById("app");
const root = createRoot(container);
root.render(<App />);
