import { useEffect, useState } from "react";
import { createRoot } from "react-dom/client";
const cohort = "2208-ftb-et-web-ft";
const strangerThings = "https://strangers-things.herokuapp.com/api";

const App = () => {
  const [allPosts, setAllPosts] = useState([]);
  const [newUser, setNewUser] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [LoginName, setLoginName] = useState('')
  const [LoginPassword, setLoginPassword] = useState('')

  //cant use API if no useEffect?
  useEffect(() => {
    const getAllData = async () => {
      await getAllPosts();
    };
    getAllData();
  }, []);

  const getAllPosts = async () => {
    const response = await fetch(`${strangerThings}/${cohort}/posts`);
    const data = await response.json();
    setAllPosts(data.data.posts);
  };

  const handleSubmit = async(event) => {
    event.preventDefault()
    console.log('name and password', newUser, newPassword)
        // console.log("registerUser");
        const response = await fetch(`${strangerThings}/${cohort}/users/register`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            user: {
              username: newUser,
              password: newPassword,
            },
          }),
        });
        const data = await response.json();
        console.log('data', data);
        console.log('data2', data.data.token)
        let token = await data.data.token

        localStorage.setItem('token', JSON.stringify(token))

        setNewUser('')
        setNewPassword('')
  }

  const loginSubmit = () => {
    const response = fetch('https://strangers-things.herokuapp.com/api/COHORT-NAME/users/login', {
  method: "POST",
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify({
    user: {
      username: LoginName,
      password: LoginPassword
    }
  })
})
console.log(response)
  }

  //register user
  // const registerUser = async () => {
  //   // console.log("registerUser");
  //   const response = await fetch(`${strangerThings}/${cohort}/users/register`, {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({
  //       user: {
  //         username: { newUser },
  //         password: { newPassword },
  //       },
  //     }),
  //   });
  //   const data = await response.json();
  //   // console.log(data);
  // };

  //registerUser();

  //submit component
  // const afterSubmit = () => {
  //   alert("Hello");
  // };

  return (
    <div>
      <h1>Hello from Stranger Things</h1>
      <div>
      <form onSubmit={handleSubmit}>
        Name:
        <input
          type='text'
          value={newUser}
          onChange={(event) => {
            setNewUser(event.target.value);
            // console.log(event.target.value);
          }}
        ></input>
        Password:
        <input
          type='text'
          value={newPassword}
          onChange={(event) => {
            setNewPassword(event.target.value);
            // console.log(event.target.value);
          }}
        ></input>
        <button type="text">Register</button>
      </form>
      </div>

      <div>
          {/* Login Submit Form */}
      <form onSubmit={loginSubmit}>
        Name:
        <input
          type='text'
          value={LoginName}
          onChange={(event) => {
            setLoginName(event.target.value);
            // console.log(event.target.value);
          }}
        ></input>
        Password:
        <input
          type='text'
          value={LoginPassword}
          onChange={(event) => {
            setLoginPassword(event.target.value);
            // console.log(event.target.value);
          }}
        ></input>
        <button type="text">Log In</button>
      </form>

      </div>
      <div>
        <h1>Title Posts</h1>
        {
          // allPosts.map(post => <div key={post._id})

          allPosts.map((post, i) => {
            return <p key={i}>{post.title}</p>;
          })
        }
      </div>
    </div>
  );
};

const container = document.getElementById("app");
const root = createRoot(container);
root.render(<App />);
