    }


  const [myResult, setmyResult] = useState({});

  const test = async () => {
    fetch(`${strangerThings}/${cohort}/users/me`, {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((response) => response.json())
      .then((result) => {
        console.log(result);
        setmyResult(result);
        // console.log("0RESULT", result);
        // console.log("1RESULT", result.data);
        // console.log("2RESULT", result.data.posts);
      })
      .catch(console.error);

  };

  test();

  return (

    <div>
    {myResult.success ? <h1>{myResult.data.username}!</h1> : <h3>Loading ...</h3>}
    {myResult.success ? <h1>{myResult.data.posts.map((me, i) => {
        return (
            <div>

            <div className="card" key={i}>
                <h2>Title: {me.title}</h2>
                <h2>Description: {me.description}</h2>
                <h2>Price: {me.price}</h2>
                <button onClick={deletePost(me._id)}>
  Delete Me
</button>




            </div>
            </div>
        )
    } )}</h1> : <h3>Loading ...</h3>}

    </div>
  )
};

export default MyProfile;
