        Name: <input value="Enter Name"></input>
        <button>Submit</button>
        Password: <input value="Enter Password"></input>
        <button>Submit</button>
      </form>
      <div>
        {allPosts.map((post, i) => {
          return;
            <p key={i}>{post.title}</p>
        })}
      </div>
    </div>
  );
};

const container = document.getElementById("app");
const root = createRoot(container);
root.render(<App />);
